(window.webpackJsonp=window.webpackJsonp||[]).push([[59],{435:function(n,r,v){"use strict";v.r(r);var _=v(8),b=Object(_.a)({},(function(){var n=this,r=n.$createElement,v=n._self._c||r;return v("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[v("h1",{attrs:{id:"peng-you-de-ding-yi"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#peng-you-de-ding-yi"}},[n._v("#")]),n._v(" 朋友的定義?")]),n._v(" "),v("blockquote",[v("p",[n._v("照這樣來說，我真的一個朋友也沒有.... 未來會不會有...未知數")])]),n._v(" "),v("p",[n._v("‎[轉] 殺人後一個孩子的做法…不看你會後悔的…"),v("br"),n._v("\n有個男孩17~18歲，家裡很有錢，整天在外面和他的朋友們吃啊，喝啊，打架。"),v("br"),n._v("\n有一天他的父親問他：「你有多少朋友」"),v("br"),n._v("\n男孩回答：「我有好多」"),v("br"),n._v("\n父親：「那你照我說的去做，先在你的白襯衫上灑點雞血，然後拿去找你的朋友說你殺人了，看看他們的反映」"),v("br"),n._v("\n男孩照這父親的話做了，去找他認為最要好的朋友，找到第一個朋友。"),v("br"),n._v("\n朋友1：「你這是怎麼了，一身血」"),v("br"),n._v("\n男孩：「兄弟，我殺人了，借點錢給我，讓我到外面躲一躲行嗎。？」"),v("br"),n._v("\n朋友1：「最近家裡的手頭實在是有點緊沒錢，要不你去找別人問問。」"),v("br"),n._v("\n男孩失望的離開了，去找他認為第二個最好的朋友。"),v("br"),n._v("\n砰砰砰~~"),v("br"),n._v("\n朋友2透過門說：「誰啊」"),v("br"),n._v("\n男孩：「是我」"),v("br"),n._v("\n依然沒有開門：「什麼事啊」"),v("br"),n._v("\n男孩：「兄弟，我殺人了，借點錢給我，讓我到外面躲一躲行嗎。？」"),v("br"),n._v("\n朋友2和第一個朋友說的一樣的話。男孩很是惱火，這個竟然連門都不開的拒絶了。"),v("br"),n._v("\n他找遍了所有的朋友都被拒絶了。"),v("br"),n._v("\n他沮喪的回到了家，向父親說明了一切。"),v("br"),n._v("\n父親：「你去找我這一個半的朋友，先去找我這半個朋友」"),v("br"),n._v("\n男孩很快就來到了，父親所說的半個朋友家，"),v("br"),n._v("\n砰砰砰。。"),v("br"),n._v("\n父親的半個朋友開門見他慌忙說：「孩子，你這是怎麼了。」"),v("br"),n._v("\n男孩：「叔叔，我殺人了」"),v("br"),n._v("\n還沒等男孩說借錢，「半個」朋友給了他一張200萬的支票，讓他出去避避風頭."),v("br"),n._v("\n男孩當時就抱這叔叔哭了起來。"),v("br"),n._v("\n那還又去找父親的所說的「一個」朋友。"),v("br"),n._v("\n砰砰砰..."),v("br"),n._v("\n「一個」朋友開門看到他渾身是血，還沒等男孩說話，一把拉進屋裡。"),v("br"),n._v("\n「孩子，你這是怎麼了」"),v("br"),n._v("\n「叔叔，我殺人了」"),v("br"),n._v("\n「趕緊把你的衣服脫下來」"),v("br"),n._v("\n「脫衣服幹什麼.?」"),v("br"),n._v("\n「我兒子和你的年齡差不多，讓他穿上，我現在就打電話報警讓他去頂罪，你就先在我這躲一段時間」"),v("br"),n._v("\n男孩當場就跪在地上痛苦起來。過了一會，向叔叔解釋了一下。"),v("br"),n._v("\n回到家以後哭着跪在父親說自己是個混蛋，發誓以後不會在交狐朋狗友了。")]),n._v(" "),v("p",[n._v("一生中只為這「一個半」朋友而奮鬥。"),v("br"),n._v("\n捨得笑，得到的是友誼；"),v("br"),n._v("\n捨得寬容，得到的是大氣；"),v("br"),n._v("\n捨得誠實，得到的是朋友；"),v("br"),n._v("\n捨得面子，得到的是實在；"),v("br"),n._v("\n捨得酒色，得到的是健康；"),v("br"),n._v("\n捨得虛名，得到的是逍遙；"),v("br"),n._v("\n捨得施捨，得到的是美名；"),v("br"),n._v("\n捨得紅塵，得到的是天尊。"),v("br"),n._v("\n捨得小，就有可能得大；"),v("br"),n._v("\n捨得近，就有可能得到遠。"),v("br"),n._v("\n捨得某種精神，就有可能得到某種物質，捨得某種物質，就有可能得到某種精神。"),v("br"),n._v("\n而有些人就是為了兩片薄面而爭，為了一條賤命而戰，一身虛榮，一身醋味，值麼？累嗎？"),v("br"),n._v("\n捨得捨得，有舍就有得；得失得失，有得就有失。人世間就是這麼奇妙，你又何須苦苦追尋一個目標"),v("br"),n._v("\n放得下，才能走得遠！有所放棄，才能有所追求."),v("br"),n._v("\n什麼也不願放棄的人，反而會失去最珍貴的東西."),v("br"),n._v("\n有一句很經典的話：當你緊握雙手，裡面什麼也沒有；當你打開雙手，世界就在你手中。懂得放棄，才能在有限的生命裡活得充實、飽滿、旺盛!")]),n._v(" "),v("p",[n._v("得之坦然!失之淡然")])])}),[],!1,null,null,null);r.default=b.exports}}]);